<?php  // hash1.php

$base = "abc";
// $base = "ab";
// $base = "";
echo "{$base} <br>\n";

// 「使ってはいけない」アルゴリズム
$h1 = md5($base);
echo "{$h1} <br>\n";
$h2 = sha1($base);
echo "{$h2} <br>\n";

// 使うならこっち
$h3 = hash("sha256", $base);
echo "{$h3} <br>\n";
$h4 = hash("sha512", $base);
echo "{$h4} <br>\n";
